import React from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import blue from '@material-ui/core/colors/blue';
import cyan from '@material-ui/core/colors/cyan';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

interface TabPanelProps {
  children?: React.ReactNode;
  index: any;
  value: any;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

function a11yProps(index: any) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}));

const theme = createMuiTheme({
    palette: {
      primary: cyan,
      secondary: {
        main: '#fafafa',
      },
    },
});

export default function SimpleTabs() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };


  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);

  return (
    <ThemeProvider theme={theme}>
        <div className={classes.root}>
        <AppBar position="static">
            <Tabs value={value} aria-label="simple tabs example" color="primary">
                <Tab color= "secondary" label="Welcome" {...a11yProps(0)} />
                <Tab color= "secondary" label="Events" {...a11yProps(1)}>
                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                    Open Menu
                </Button>
                <Menu
                    id="simple-menu"
                    keepMounted
                    open={Boolean(anchorEl)}
                >
                    <MenuItem>Profile</MenuItem>
                    <MenuItem>My account</MenuItem>
                    <MenuItem>Logout</MenuItem>
                </Menu>
                </Tab>
                <Tab color= "secondary" label="Galery" {...a11yProps(2)} />
                <Tab color= "secondary" label="Login" {...a11yProps(3)} />
                <Tab color= "secondary" label="Register" {...a11yProps(4)} />
            </Tabs>
        </AppBar>
        <TabPanel value={value} index={0}>
            Homepage
        </TabPanel>
        <TabPanel value={value} index={1}>
            Events coming soon...
        </TabPanel>
        <TabPanel value={value} index={2}>
            Photos coming soon...
        </TabPanel>
        </div>
    </ThemeProvider>
  );
}
